import "./contact_style.css"
import { useState } from "react";

export default function ContactForm(){
    const [hasChildren, setHasChildren] = useState(false);
    const [childrenQuantity, setChildrenQuantity] = useState('');

    const handleChildrenChange = (e) => {
        setHasChildren(e.target.value === 'yes');
    };

    function childrenData(n){
        
            for(let i=0; i<n; i++){
                return(                
                    <div>
                            <input placeholder="Age"/>
                            <label>
                            <input type="radio" value="M" />
                            M
                            </label>
                            <label>
                            <input type="radio" value="F"/>
                            F
                            </label>
                            <label>
                            <input type="radio" value="M"/>
                            Other
                            </label>
                        </div>
                    );
            }
           
    }


    return(
        <div className="form-container">
            <h1>Not sure if Verne has what you are looking for? Send Us a Message</h1>
            <form>
                <input placeholder="Name"/>
                <input placeholder="Surname"/>
                <input placeholder="Age"/>
                {/* Dropdown menu for selecting the type of romantic adventure */}
                <select>
                    <option value="" disabled selected  hidden={true}>Choose Your Love Adventure</option>
                    <option value="beach">Romantic Beach</option>
                    <option value="mountain">Exciting Mountain Expedition</option>
                    <option value="spa">Luxury Relaxing Spa</option>
                    <option value="museum">Lovely Cultural City</option>
                    <option value="extreme">Extreme Breathless Adventure</option>
                </select>
                <input placeholder="Email"/>
                <input placeholder="Phone Number"/>

                {/* Radio button for asking if there are children */}
                <div>

                    <label>Do you have children?</label>
                    <label>
                        <input
                            type="radio"
                            value="yes"
                            checked={hasChildren}
                            onChange={handleChildrenChange}
                        />
                        Yes
                    </label>
                    <label>
                        <input
                            type="radio"
                            value="no"
                            checked={!hasChildren}
                            onChange={handleChildrenChange}
                        />
                        No
                    </label>
                </div>

                {/* Input for the quantity of children (visible only if hasChildren is true) */}
                {hasChildren && (
                    <><input
                        placeholder="Number of Children"
                        value={childrenQuantity}
                        onChange={(e) => setChildrenQuantity(e.target.value)}
                    />
                    {childrenData(childrenQuantity)}
                    </>
                )}

            </form>
        </div>
    );
}
