import Navbar from "../components/NavBar";
import fotocamera from "../asserts/fotocamera.jpg";

import "./msu_style.css";
import { useState } from "react";



export default function MobileSignUp(){
    const [state, setState] = useState("mobile-not-show");
    const [show, setShow]= ise
    return (
        <>
            <Navbar/>
            <div className="mobile-container">
                <div className="mobile-sign-up">
                    <h1>Sign Up to Verne</h1>
                <form>
                    <label >Name</label>
                    <input placeholder="Name"/>
                    <label >Email</label>
                    <input type='email' placeholder="example@domain.com"/>
                    <label >Password</label>
                    <input type='password' placeholder="Password"/>
                </form>
                </div>
                <div  className={state} fotocamera >
                    <img src={fotocamera} alt="foto" />
                </div>
                <button onClick={()=>setState("mobile-show")}>Sign Up with Documents <i className="fa-solid fa-camera" /></button>
                <button className="close-button" onClick={()=>setState("mobile-not-show")}>Close Camera</button>
            </div>
        </>
    );
}
