//import css-style
import  "./navbar_style.css";
import { Link } from "react-router-dom";
import { MenuItems } from "./MenuItems";
import { useState } from "react";
import Model from 'react-model';



export default function Navbar(){
    const [state, setState] = useState({clicked:false});
    const [visible, setVisible] = useState(falese);
    

    function handleClick(){
        setState({clicked: !state.clicked})
    }

    return(
        <nav className="NavbarItems">
            <h1 className="navbar-logo">Verne</h1>
            
            <div className="menu-icon" onClick={handleClick}>
                <i className={state.clicked ? "fas fa-times":"fas fa-bars"}></i>
            </div>
            
            <ul className={state.clicked ? "nav-menu active":"nav-menu"}>
            {/* map through the menu items */}{
                MenuItems.map((item, index) => {
                    return(
                        <li key={index} >
                            <Link to={item.url} className={item.cName}><i className={item.icon}></i>{item.title}</Link>
                        </li>
                    );
                })}

                <button on>Sign Up</button>
                <Model isOpen={visible}>
                    <h1>Modello </h1>
                </Model>
            </ul>
        </nav>
    )
    
}
