//import css-style
import  "./navbar_style.css";
import { Link } from "react-router-dom";
import { MenuItems } from "./MenuItems";
import { useState } from "react";




export default function Navbar(){
    const [state, setState] = useState({clicked:false});
    const [visible, setVisible] = useState(false);
    

    function handleClick(){
        setState({clicked: !state.clicked})
    }

    function toggeModal(){
        setVisible(!visible)
    }

    return(
        <nav className="NavbarItems">
            <h1 className="navbar-logo">Verne</h1>
            
            <div className="menu-icon" onClick={handleClick}>
                <i className={state.clicked ? "fas fa-times":"fas fa-bars"}></i>
            </div>
            
            <ul className={state.clicked ? "nav-menu active":"nav-menu"}>
            {/* map through the menu items */}{
                MenuItems.map((item, index) => {
                    return(
                        <li key={index} >
                            <Link to={item.url} className={item.cName}><i className={item.icon}></i>{item.title}</Link>
                        </li>
                    );
                })}
                <div className="modal">
                    <button onClick={toggeModal} className="btn-modal">Sign Up</button>
                    <div className="overlay" onClick={toggeModal}></div>
                        <div className="modal-content">
                            <h1>Sign Up To Verne</h1>
                            <p>Iscriviti al nostro sito perch√® lo dico io</p>
                            <button className="close-modal" onClick={()=>setVisible(!visible)}>Close</button>
                        </div>               
                </div>
            </ul>
        </nav>
    )
    
}
